import{_ as s,o as n,c as e,i as a}from"./app.4e6a740b.js";const F=JSON.parse('{"title":"\u{1F4DD} Extension Manifest","description":"Learn about the structure of an extension manifest.","frontmatter":{"title":"\u{1F4DD} Extension Manifest","description":"Learn about the structure of an extension manifest.","sidebar":"extensions"},"headers":[{"level":2,"title":"name","slug":"name"},{"level":2,"title":"description","slug":"description"},{"level":2,"title":"icon","slug":"icon"},{"level":2,"title":"author","slug":"author"},{"level":2,"title":"releaseTimestamp","slug":"releasetimestamp"},{"level":2,"title":"version","slug":"version"},{"level":2,"title":"id","slug":"id"},{"level":2,"title":"readme","slug":"readme"},{"level":2,"title":"tags","slug":"tags"},{"level":2,"title":"target","slug":"target"},{"level":2,"title":"dependencies","slug":"dependencies"},{"level":2,"title":"compiler","slug":"compiler"},{"level":2,"title":"contributeFiles","slug":"contributefiles"},{"level":2,"title":"compatibleAppVersions","slug":"compatibleappversions"}],"relativePath":"extensions/extension-manifest.md","lastUpdated":1660824437000}'),o={name:"extensions/extension-manifest.md"},l=a(`<h1 id="\u{1F4DD}-extension-manifest" tabindex="-1">\u{1F4DD} Extension Manifest <a class="header-anchor" href="#\u{1F4DD}-extension-manifest" aria-hidden="true">#</a></h1><p>The extension manifest is used by bridge. to register the extension so it can be used. It contains important information that bridge. requires and this information is described below.</p><h2 id="name" tabindex="-1">name <a class="header-anchor" href="#name" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u2705</li></ul><p>This defines the name of your extension and will be shown in bridge.&#39;s extension store UI.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">name</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">My Extension</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="description" tabindex="-1">description <a class="header-anchor" href="#description" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u2705</li></ul><p>This field should describe what your extension does in some detail. It will be displayed in bridge.&#39;s extension store UI, underneath the <a href="#name"><code>name</code></a>.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">description</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">This extensions adds...</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="icon" tabindex="-1">icon <a class="header-anchor" href="#icon" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u{1F6AB}</li></ul><p>This accepts the <a href="/extensions/misc/icons/">standard icon format</a> that bridge. uses across its UI. This will be shown next to the <a href="#name"><code>name</code></a> of the extension.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">icon</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">mdi-cloud</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="author" tabindex="-1">author <a class="header-anchor" href="#author" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u2705</li></ul><p>This defines the author of the extension, which should be your name! This will be show in bridge.&#39;s extension store UI at the start of the <a href="#tags"><code>tags</code></a> list. You should keep this name consistent between all of your extensions to allow users to easily filter by your extensions.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">author</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">My Name</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="releasetimestamp" tabindex="-1">releaseTimestamp <a class="header-anchor" href="#releasetimestamp" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u2705</li></ul><p><strong>This field shouldn&#39;t be added by the user.</strong> When your extension is published, this will automatically be added to your manifest. When updating your extension, you shouldn&#39;t modify this field. It is used by bridge. to sort extensions to put newest first.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">releaseTimestamp</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#F78C6C;">1620755291316</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="version" tabindex="-1">version <a class="header-anchor" href="#version" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u2705</li></ul><p>The version of your extension should be a <a href="https://semver.org/" target="_blank" rel="noreferrer">semantic version</a> string. When you want to publish an update your extension, you should increase the version number.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>When publishing your extension for the first time, it makes sense to set the version to <code>1.0.0</code></p></div><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">version</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">1.0.0</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="id" tabindex="-1">id <a class="header-anchor" href="#id" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u2705</li></ul><p>Your extension id must be unique to your extension and must remain the same between extension updates. In order to keep your extension&#39;s id unique, you should generate a <a href="https://www.uuidgenerator.net/version4" target="_blank" rel="noreferrer">uuid</a> for it.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">id</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">e77ac43d-1172-47ec-a09f-3537689e6912</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="readme" tabindex="-1">readme <a class="header-anchor" href="#readme" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u{1F6AB}</li></ul><p>This allows you to set a link to where users can read more about about your extension. This could be the url to your own website or to a readme in a GitHub repository. It will create a button on your extension, in the extension store, that the user can press to go to the url you have specified here.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">readme</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">https://github.com/bridge-core/plugins/blob/master/plugins/Templater/README.md</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="tags" tabindex="-1">tags <a class="header-anchor" href="#tags" aria-hidden="true">#</a></h2><ul><li>Type: <code>string[]</code></li><li>Required: \u2705</li></ul><p>This field allows you to assign tags to your extension. A tag is shown as a colored label underneath your extension listing in the store and a user can filter extensions by tag, therefore your tags must be carefully chosen to represent the content of your extension.</p><p>The available tags are the following: <code>Curated</code>, <code>Verified</code>, <code>Utility</code>, <code>Theme</code>, <code>Snippets</code>, <code>Component</code>, <code>Command</code>, <code>Presets</code>, <code>Compiler</code></p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">tags</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">Theme</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">]</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="target" tabindex="-1">target <a class="header-anchor" href="#target" aria-hidden="true">#</a></h2><ul><li>Type: <code>string</code></li><li>Required: \u2705</li></ul><p>Though this field isn&#39;t required for your extension to show in bridge., it is essential to add when publishing it to the extension store. This is because the extension API differs between bridge. v2 and bridge. v1, so you must specify which major app version this extension is for. As this documentation is designed for bridge. v2, it is recommended that you set this field to <code>&quot;v2&quot;</code>. The available options are: <code>&quot;both&quot;</code>, <code>&quot;v2&quot;</code> or <code>&quot;v1&quot;</code>.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">target</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">v2</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="dependencies" tabindex="-1">dependencies <a class="header-anchor" href="#dependencies" aria-hidden="true">#</a></h2><ul><li>Type: <code>string[]</code></li><li>Required: \u{1F6AB}</li></ul><p>This property allows you to define which other extensions should be loaded before your extension in case your extension depends on the functionality of another extension. You can reference an extension in this list by its <a href="#id"><code>id</code></a>.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">dependencies</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">2959c702-d3a4-4e69-94ce-4ad18e9f816a</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">]</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="compiler" tabindex="-1">compiler <a class="header-anchor" href="#compiler" aria-hidden="true">#</a></h2><ul><li>Type: <code>{ plugins: Record&lt;string, string&gt; }</code></li><li>Required: \u{1F6AB}</li></ul><p>The <code>compiler</code> should be used exclusivley for extensions containing <a href="/extensions/compiler-plugins.html">compiler plugins</a>. It is used to register the extension to Dash so that the user can use your plugin by referencing its id in their <a href="/guide/misc/project-config.html">project config</a> or <a href="/guide/advanced/dash/index.html#adding-new-profiles">compiler config</a> file. It should be an object containing a <code>plugins</code> property which contains key/value pairs of unique plugin identifiers to file path relative to the root of the extension.</p><p>The example below would have the following folder structure:</p><div class="language-"><button class="copy"></button><span class="lang"></span><pre><code><span class="line"><span style="color:#A6ACCD;">.</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500 \u{1F4C1} myExtension</span></span>
<span class="line"><span style="color:#A6ACCD;">\u2502  \u251C\u2500 \u{1F4DD} manifest.json</span></span>
<span class="line"><span style="color:#A6ACCD;">\u2502  \u2514\u2500 \u{1F4C1} compiler</span></span>
<span class="line"><span style="color:#A6ACCD;">\u2502     \u2514\u2500 \u{1F4C4} main.js</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">compiler</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">&quot;</span><span style="color:#FFCB6B;">plugins</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">			</span><span style="color:#89DDFF;">&quot;</span><span style="color:#F78C6C;">myPluginId</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">compiler/main.js</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="contributefiles" tabindex="-1">contributeFiles <a class="header-anchor" href="#contributefiles" aria-hidden="true">#</a></h2><ul><li>Type: <code>Record&lt;string, { pack: TPackTypeId; path: string }&gt;</code></li><li>Required: \u{1F6AB}</li></ul><p>Sometimes your extension may need to add files to the user&#39;s project and that is where this field is used; it should define what and where to copy a file or folder. The source can either be the path to a file or folder relative to the extension root that you want to copy to the user&#39;s project. The destination should be an object with <code>pack</code> and <code>path</code> properties. <code>pack</code> should be a <a href="/extensions/misc/pack-types.html">pack type id</a> specifying which pack type to copy to and <code>path</code> should be the path to the destination relative to the root of the pack specified.</p><p>Internally, this field works by checking the extension for a <code>.installed</code> file; if it exists then the extension is already installed and nothing will happen. When the file isn&#39;t present, bridge. will install the files to the project. You should <strong>delete this file to in order to trigger bridge.&#39;s file installation</strong> and test this field.</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>The main purpose of this field is to add <a href="/guide/advanced/custom-components/">custom components</a>, <a href="/guide/advanced/custom-commands/">custom commands</a> and <a href="/guide/advanced/molang-files/">custom molang files</a> to the user&#39;s project.</p></div><p>Copying over files:</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">contributeFiles</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">&quot;</span><span style="color:#FFCB6B;">components/item/myComponent.js</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">			</span><span style="color:#89DDFF;">&quot;</span><span style="color:#F78C6C;">pack</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">behaviorPack</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">			</span><span style="color:#89DDFF;">&quot;</span><span style="color:#F78C6C;">path</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">components/item/myComponent.js</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>Copying over files:</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">contributeFiles</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">&quot;</span><span style="color:#FFCB6B;">components/item</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">			</span><span style="color:#89DDFF;">&quot;</span><span style="color:#F78C6C;">pack</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">behaviorPack</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">			</span><span style="color:#89DDFF;">&quot;</span><span style="color:#F78C6C;">path</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">components/item/</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="compatibleappversions" tabindex="-1">compatibleAppVersions <a class="header-anchor" href="#compatibleappversions" aria-hidden="true">#</a></h2><ul><li>Type: <code>{ min?: string; max?: string }</code></li><li>Required: \u{1F6AB}</li></ul><p>This field allows you to specify the range of bridge. versions that your extension is available for. This is used to stop users from using an extension that doesn&#39;t work on their bridge. version. Incompatibilities with bridge. versions can happen when an extension utilizes a new extension capability or uses old formats.</p><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">compatibleAppVersions</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">		</span><span style="color:#89DDFF;">&quot;</span><span style="color:#FFCB6B;">min</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">2.3.0</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div>`,67),t=[l];function p(i,c,r,d,D,u){return n(),e("div",null,t)}var h=s(o,[["render",p]]);export{F as __pageData,h as default};
